package operatorex;

public class Assignmentoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =10;
		int b=20;
		int c=10;
		int f;
		a+=10;//a=a+10;
		b-=a;//b=b-a;
		a++;//21
		b++;//1
		b+=c;//b=b+c;=11
		c--;//9
		f=--a;//a-1=a
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(f);
		
	}

}
