package simple_examples;

public class Swap4with {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		int a = 2;
		int b = 3;
		int c = 4;
		int d = 1;
		int x=0;
		System.out.println("A:"+a+"\tB:"+b+"\tC:"+c+"\tD:"+d);
		
		x=a;
		a=b;
		b=c;
		c=d;
		d=x;
		System.out.println("A:"+a+"\tB:"+b+"\tC:"+c+"\tD:"+d);
		
	}

}
